create schema smdproject;
create table  smdproject.HubMetaData (sequence varchar(50) NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,primary key(sequence,timestamp,source));
create table  smdproject.HubSubject (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,name varchar(50),primary key(sequence,timestamp,source));
create table  smdproject.HubExperimentalUnit (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,primary key(sequence,timestamp,source));
create table  smdproject.HubSession (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,primary key(sequence,timestamp,source));
create table  smdproject.HubObservation (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,collectedAtSession varchar(50) ,primary key(sequence,timestamp,source),foreign key(collectedAtSession) references smdproject.HubSession(sequence));
create table  smdproject.HubExperiment (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,primary key(sequence,timestamp,source));
create table  smdproject.HubTreatment (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,experiment varchar(50) NOT NULL,primary key(sequence,timestamp,source),foreign key(experiment) references smdproject.HubExperiment(sequence));
create table  smdproject.HubGroup (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,treatment varchar(50) NOT NULL,primary key(sequence,timestamp,source),foreign key(treatment) references smdproject.HubTreatment(sequence));
create table  smdproject.HubFactor (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,experiment varchar(50) NOT NULL,isCofactor int DEFAULT 0,primary key(sequence,timestamp,source),foreign key(experiment) references smdproject.HubExperiment(sequence));
create table  smdproject.SatExperimentTitle (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,title varchar(255),primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubExperiment(sequence));
create table  smdproject.SatExperimentAcronym (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,acronym varchar(15),primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubExperiment(sequence));
create table  smdproject.SatFactorName (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,name varchar(40),primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubFactor(sequence));
create table  smdproject.SatFactorLevel (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,levelValue varchar(40),primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubFactor(sequence));
create table  smdproject.SatObservationName (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,name varchar(400),primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubObservation(sequence));
create table  smdproject.SatObservationValue (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,value TEXT ARRAY,timestamps TEXT ARRAY,primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubObservation(sequence));
create table  smdproject.SatSubjectAge (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,age integer,primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubSubject(sequence));
create table  smdproject.SatSubjectName (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,name varchar(40),primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubSubject(sequence));
create table  smdproject.SatSessionName (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,name varchar(40),primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubSession(sequence));
create table  smdproject.SatMetaDataKeyValuePair (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,key varchar(40) NOT NULL,value varchar(50),primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubMetaData(sequence));
create table  smdproject.SatTreatmentFactorLevel (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,factorLevel varchar(40) NOT NULL,primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubTreatment(sequence));
create table  smdproject.ObservationMetaData (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,Observation varchar(50) NOT NULL,metadata varchar(40) ,primary key(sequence,timestamp,source),foreign key(Observation) references smdproject.HubObservation(sequence),foreign key(metadata) references smdproject.HubMetaData(sequence));
create table  smdproject.SessionMetaData (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,session varchar(50) NOT NULL,metadata varchar(40) NOT NULL,primary key(sequence,timestamp,source),foreign key(session) references smdproject.HubSession(sequence),foreign key(metadata) references smdproject.HubMetaData(sequence));
create table  smdproject.SatGroupName (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,name varchar(40) NOT NULL,primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.HubGroup(sequence));
create table  smdproject.AssignedTO (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,experimentalUnit varchar(50) NOT NULL,grop varchar(50)	NOT NULL,primary key(sequence,timestamp,source),foreign key(grop) references smdproject.HubGroup(sequence),foreign key(experimentalUnit) references smdproject.HubExperimentalUnit(sequence));
create table  smdproject.ParticipatesIn (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,experimentalUnit varchar(50) NOT NULL,experiment varchar(50) NOT NULL,primary key(sequence,timestamp,source),foreign key(experiment) references smdproject.HubExperiment(sequence),foreign key(experimentalUnit) references smdproject.HubExperimentalUnit(sequence));
create table  smdproject.SatExperimentalUnitIdentifier (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,ID varchar(15),primary key(sequence,timestamp,source),foreign key(sequence) references smdproject.ParticipatesIn(sequence));
create table  smdproject.AttendsSession (sequence varchar(50)	NOT NULL UNIQUE,timestamp time NOT NULL,source varchar(50) NOT NULL,experimentalUnit varchar(50) NOT NULL,grop varchar(50) ,session varchar(50) NOT NULL,primary key(sequence,timestamp,source),foreign key(grop) references smdproject.HubGroup(sequence),foreign key(experimentalUnit) references smdproject.HubExperimentalUnit(sequence),foreign key(session) references smdproject.HubSession(sequence));